import React, { useState, useEffect } from "react";
import "./NavBar.css";
import { FaLinkedin, FaGithub } from "react-icons/fa";

// Function to scroll smoothly to a section
const scrollToSection = (id: string) => {
  const section = document.getElementById(id);
  if (section) {
    section.scrollIntoView({ behavior: "smooth" });
  }
};

function NavBar() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [navTransparent, setNavTransparent] = useState(false);

  // Detect scroll and change navbar transparency
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setNavTransparent(true);
      } else {
        setNavTransparent(false);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleLinkClick = (id: string) => {
    setMenuOpen(false);
    scrollToSection(id);
  };

  return (
    <>
      <div className={`NavBarStart ${navTransparent ? "transparent" : ""}`}>
        <div className="block block1"></div>

        {/* Hamburger Menu Button */}
        <button className="hamburger" onClick={() => setMenuOpen(!menuOpen)}>
          â˜°
        </button>

        {/* Navigation Links */}
        <div className={`block block2 ${menuOpen ? "open" : ""}`}>
          <button className="navBlock NavLink" onClick={() => handleLinkClick("Home")}>
            Home
          </button>
          <button className="navBlock NavLink" onClick={() => handleLinkClick("Publication")}>
            Publications
          </button>
          <button className="navBlock NavLink" onClick={() => handleLinkClick("Publication")}>
            Projects
          </button>
          <button className="navBlock NavLink" onClick={() => handleLinkClick("Publication")}>
            Get in Touch
          </button>
          <button className="navBlock NavLink" onClick={() => handleLinkClick("Publication")}>
            Full Resume
          </button>
          <div className="navBlock navBlock3 NavLink">
            <a href="https://www.linkedin.com/in/nvsahana/" target="_blank" rel="noopener noreferrer">
              <FaLinkedin size={30} />
            </a>
            <a href="https://github.com/nvsahana" target="_blank" rel="noopener noreferrer">
              <FaGithub size={30} />
            </a>
          </div>
        </div>

        <div className="block block3"></div>
      </div>
    </>
  );
}

export default NavBar;
